mapper.select:
select T.rno, T.userName, T.qnaSeq, T.userSeq, T.qnaInquiry, T.qnaSubject ,T.qnaContent, T.qnaRegDate, T.qnaProgress, T.fk_seq, T.qnaStatus, T.userStatus from ( select rownum as rno, V.userName, V.qnaSeq, V.userSeq, V.qnaInquiry, V.qnaSubject , V.qnaContent, V.qnaRegDate, V.qnaProgress, V.fk_seq, V.qnaStatus, V.userStatus from ( select b.userName, a.qnaSeq, b.userSeq, a.qnaInquiry, a.qnaSubject , a.qnaContent, a.qnaRegDate, a.qnaProgress, a.fk_seq, a.qnaStatus, b.userStatus from tbl_qna a join tbl_user b on a.userSeq = b.userSeq where a.qnaStatus = 0 and b.userStatus = 0
and a.userSeq = #{userSeq}
and to_char(a.qnaRegDate,'YYYY-MM-DD hh24:mi:ss') between to_char(to_date(#{searchStartDay} || '00:00:00', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss') and to_char(to_date(#{searchEndDay} || '23:59:59', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss')
and ${qnaColName} like '%' || #{qnaSearch} || '%'
and qnaInquiry like '%' || #{qnaInquiry} || '%'
and qnaProgress = #{qnaProgress}
order by a.qnaRegDate asc )V )T where #{start} <= rno and rno <= #{end}

select count(*) from tbl_qna a join tbl_user b on a.userSeq = b.userSeq where a.qnaStatus = 0 and b.userStatus = 0
and a.userSeq = #{userSeq}
and to_char(qnaRegDate,'YYYY-MM-DD hh24:mi:ss') between to_char(to_date(#{searchStartDay} || '00:00:00', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss') and to_char(to_date(#{searchEndDay} || '23:59:59', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss')
and ${qnaColName} like '%' || #{qnaSearch} || '%'
and qnaInquiry like '%' || #{qnaInquiry} || '%'
and qnaProgress = #{qnaProgress}

select count(*) from tbl_qna a join tbl_user b on a.userSeq = b.userSeq where a.qnaStatus = 0 and b.userStatus = 0 and a.qnaProgress = #{qnaProgress}
and a.userSeq = #{userSeq}
and to_char(qnaRegDate,'YYYY-MM-DD hh24:mi:ss') between to_char(to_date(#{searchStartDay} || '00:00:00', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss') and to_char(to_date(#{searchEndDay} || '23:59:59', 'YYYY-MM-DD hh24:mi:ss'),'YYYY-MM-DD hh24:mi:ss')
and ${qnaColName} like '%' || #{qnaSearch} || '%'
and qnaInquiry like '%' || #{qnaInquiry} || '%'
