mapper.select:
select revImgSeq, reviewSeq, reviewImg from ( select V.* , rank ( ) over ( partition by reviewSeq order by revImgSeq desc) as num from tbl_review_image V where reviewImageStatus = 0) where num = 1 and reviewSeq in
${seqList[i]}
order by reviewSeq desc

select revImgSeq, reviewSeq, reviewImg
from tbl_review_Image
where reviewImageStatus = 0 and reviewSeq = #{reviewSeq}

select reviewImg
from tbl_review_Image
where reviewImageStatus = 0 and revImgSeq = #{revImgSeq}

select max(reviewSeq)
from tbl_review
where userSeq = #{userSeq}
and restSeq = #{restSeq}

select count(*)
from tbl_review
where restSeq = #{restSeq}

  select *
	from(
	select A.*, B.restName, B.restBgTag, B.restMdTag, substr(B.restAddr,4,7) as restAddr, rownum as rnum
	from(
	select A.*, B.userName, B.userProfile
	from(
	select A.reviewImg, B.reviewSeq, B.userSeq, B.restSeq, B.reviewTitle, substr(B.reviewContent,1,10)||'..' as reviewContent, B.reviewAvgScore
	from (
	select max(reviewSeq) as reviewSeq, reviewImg
	from tbl_review_Image
	where reviewImageStatus = 0
	group by reviewSeq,reviewImg
	           )A join tbl_review B
	           on A.reviewSeq = B.reviewSeq
	           where B.reviewStatus = 0
	           order by A.reviewSeq desc) A join tbl_user B
	           on A.userSeq = B.userSeq
	           where userSort = 0)A join tbl_restaurant B
	           on A.restSeq = B.restSeq
	           )
	where rnum between #{StartRno} and #{EndRno}

select reviewHit
from tbl_review
where reviewSeq = #{reviewSeq}

select reviewSeq
from tbl_review_Liker
where userSeq = #{userSeq}

select count(*)
from tbl_review
where userSeq = #{userSeq} and restSeq = #{restSeq}
